<?phpfunction getListeMembres(){	$db = Base::getConnection();	$query = 'SELECT user_id, user_avatar, username	FROM phpbb_users	WHERE group_id != 6	AND group_id != 1';	$tab = array();	foreach($db->query($query) as $row)	{		$tab[$row['user_id']] = $row;		$query = 'SELECT COUNT(*) c FROM collection_possede_jeu WHERE user_id='.$row['user_id'];		$tab[$row['user_id']]['nbJ']=0;		foreach($db->query($query) as $r)		{			$tab[$row['user_id']]['nbJ']=$r['c'];		}		$query = 'SELECT COUNT(*) c FROM collection_possede_console WHERE user_id='.$row['user_id'];		$tab[$row['user_id']]['nbC']=0;		foreach($db->query($query) as $r)		{			$tab[$row['user_id']]['nbC']=$r['c'];		}		$query = 'SELECT COUNT(*) c FROM collection_possede_accessoire WHERE user_id='.$row['user_id'];		$tab[$row['user_id']]['nbA']=0;		foreach($db->query($query) as $r)		{			$tab[$row['user_id']]['nbA']=$r['c'];		}		$query = 'SELECT * FROM user_stats WHERE user_id='.$row['user_id'];		$tab[$row['user_id']]['points']=0;		foreach($db->query($query) as $r)		{			$tab[$row['user_id']]['points']=$r['modifs_jeux']+$r['modifs_consoles']+$r['modifs_accessoires']+$r['ajouts_jeux']+$r['ajouts_consoles']+$r['ajouts_accessoires']+$r['modifs_versions_jeux']+$r['modifs_versions_consoles']+$r['modifs_versions_accessoires']+$r['ajouts_versions_jeux']+$r['ajouts_versions_consoles']+$r['ajouts_versions_accessoires'];		}		$tab[$row['user_id']]['user_avatar']='./forum/download/file.php?avatar='.$tab[$row['user_id']]['user_avatar'];			} 	return $tab;}function findUsername($id){	$db = Base::getConnection();	$query = 'SELECT username	FROM phpbb_users	WHERE user_id='.$id;	$tab = array();	foreach($db->query($query) as $row)	{		return $row['username'];	} 	return "";}?>