<?phpinclude 'photo.php';include '../bddjv/base.php';if (!empty($_FILES)) {	if(!is_dir('../img/collection/'.$_POST['user_id']))	{		mkdir('../img/collection/'.$_POST['user_id']);	}	$targetPath = 'img/collection/'.$_POST['user_id'].'/';   //2    $tempFile = $_FILES['file']['tmp_name'];          //3       	if(!is_file($targetPath. $_FILES['file']['name']))	{		$targetFile =  $targetPath. $_FILES['file']['name'];  //5		$photo = new Photo();		$photo->setAttr('url_photo',$targetFile);		$photo->setAttr('id_album', $_POST['id_album']);		$photo->setAttr('nom_photo', "");		$photo->setAttr('description_photo',"");		$photo->save();		move_uploaded_file($tempFile,'../'.$targetFile); //6   	}}?> 